import pandas as pd

def recent_usage_trend(row, df_train, aux_dataframes):
    usage_df = aux_dataframes['usage_data_table.csv']
    customer_usage = usage_df.loc[(usage_df['customer_id'] == row['customer_id']) &
                                  (pd.to_datetime(usage_df['date']) > pd.to_datetime('2025-01-15'))]
    if customer_usage.empty:
        return 0
    return customer_usage['data_consumed'].sum()

